INSERT INTO "ProductFunction" ("id", "name", "nameCn", "descriptionEn", "descriptionCn", "featureId", "tags") VALUES
('PF-0001', 'Call Answering and Rejection', '接听与拒接电话', 'Handles incoming call responses, including answering, rejecting, and managing multiple calls.', '处理来电响应，包括接听、拒接和管理多个通话。', 'F012', ARRAY['communication','infotainment','telematics','user-triggered','notification','convenience']::text[]),
('PF-0002', 'Bluetooth Connection Control', '蓝牙连接控制', 'Manage Bluetooth connection establishment, disconnection, and device pairing, including manual and automatic operations.', '管理蓝牙连接的建立、断开和设备配对，包括手动和自动操作。', 'F025', ARRAY['Convenience','Communication','Infotainment','Telematics','Manual-control','Automation','User-triggered','System-triggered']::text[]),
('PF-0003', 'Front Collision Warning', '前碰撞预警', 'Manages front collision warning functions including detection, sensitivity settings, warning notifications, and system state display.', '管理前碰撞预警功能，包括检测、灵敏度设置、警告通知和系统状态显示。', 'F011', ARRAY['safety','driving','ADAS','awareness','notification','system-triggered','condition-based']::text[]),
('PF-0004', 'Driving Event Recording', '驾驶事件记录', 'Records vehicle state and driver operation information during specific driving events (e.g., emergency braking) for safety analysis and service purposes.', '在特定驾驶事件（如紧急制动）期间记录车辆状态和驾驶员操作信息，用于安全分析和服务目的。', 'F011', ARRAY['safety','driving','emergency','ADAS','telematics','monitoring','event-based','security']::text[]),
('PF-0005', 'Driver Fatigue Alert', '驾驶员疲劳提醒', 'Monitors driver fatigue levels and provides alerts through visual, auditory, and haptic feedback to encourage rest breaks.', '监测驾驶员疲劳程度，通过视觉、听觉和触觉反馈提醒驾驶员休息。', 'F004', ARRAY['safety','driving','ADAS','awareness','feedback','monitoring','system-triggered','condition-based']::text[]),
('PF-0006', 'Safe Exit Assistance', '安全离车辅助', 'Detects approaching vehicles from behind and warns passengers when opening doors to prevent accidents during exit.', '检测后方接近的车辆，在乘客开门时发出警告，防止离车时发生事故。', 'F030', ARRAY['safety','driving','ADAS','awareness','notification','system-triggered','condition-based']::text[]),
('PF-0007', 'Camera View Management', '摄像头视图管理', 'Controls the switching and display of various camera views (e.g., rearview, surround view) and related monitoring features to enhance visibility during driving and parking.', '控制各种摄像头视图（如后视、环视）的切换和显示，以及相关监测功能，以提升驾驶和停车时的可见性。', 'F005', ARRAY['Safety','Convenience','Driving','Parking','IVI','ADAS','Body-Control','Awareness','Monitoring','User-Triggered','System-Triggered','Condition-Based']::text[]),
('PF-0008', 'Parking Interface & Remote Interaction', '泊车交互与远程控制', 'Handles user interactions, display of parking information (spots, trajectory), and function activation/deactivation via smartphone or control panels.', '负责处理用户交互、泊车信息（车位、轨迹）的显示，以及通过智能手机或控制面板进行的功能激活与关闭。', 'F014', ARRAY['Convenience','Parking','Mobile-App','Infotainment','ADAS','Manual-Control','User-Triggered','Information','Remote']::text[]),
('PF-0009', 'Remote Control Activation & Settings', '远程控制开启与设置', 'Manages the enabling, disabling, and general configuration of remote control capabilities via smartphone apps or vehicle switches (SWP), including status notifications and failure alerts.', '管理通过智能手机应用程序或车辆开关（SWP）启用、禁用和配置远程控制功能，包括状态通知和故障警报。', 'F008', ARRAY['Convenience','Remote','Mobile-App','Body-Control','Telematics','Notification','Manual-Control','User-Triggered']::text[]),
('PF-0010', 'Parking & Holding Assistance', '驻车与保持辅助', 'Manages vehicle stationary control including Automatic Vehicle Hold (AVH), Hill Hold Control (HHC), and Electronic Parking Brake (EPB) operations.', '管理车辆静止控制功能，包括自动驻车(AVH)、坡道辅助(HHC)和电子手刹(EPB)的操作与逻辑。', 'F014', ARRAY['Safety','Convenience','Parking','Driving','Automation','ADAS','Powertrain','Body-control','Automation','System-triggered','Condition-based']::text[]),
('PF-0011', 'Traffic Light Alert Control', '交通信号灯警报控制', 'Allows users to manage audio notifications for traffic light changes, including turning alerts on or off via settings.', '允许用户管理交通信号灯变化的音频通知，包括通过设置打开或关闭警报。', 'F003', ARRAY['Safety','Driving','Audio','ADAS','Notification','User-triggered','System-triggered','Awareness']::text[]),
('PF-0012', 'Traffic Sign Recognition Status and Control', '交通标志识别状态与控制', 'Provides status notifications, fault alerts, and control functions for the Traffic Sign Recognition system, including sensor issues, environmental impacts, and system self-test results.', '提供交通标志识别系统的状态通知、故障警报和控制功能，包括传感器问题、环境影响和系统自检结果。', 'F013', ARRAY['Safety','Navigation','Driving','ADAS','Infotainment','Notification','Monitoring','System-triggered','Condition-based','Information']::text[]),
('PF-0013', 'Lighting Control', '灯光控制', 'Controls and monitors vehicle lighting systems including adaptive high beams and other lighting features', '控制和监控车辆灯光系统，包括自适应远近光灯和其他灯光功能', 'F008', ARRAY['Safety','Convenience','Driving','Automation','Lighting','Monitoring','System-Triggered','Condition-Based']::text[]),
('PF-0014', 'Cruise Speed Control', '巡航车速控制', 'Allows the driver to set and adjust cruise speed based on navigation data and system suggestions', '允许驾驶员根据导航数据和系统建议设置和调整巡航车速', 'F013', ARRAY['Comfort','Safety','Navigation','Driving','ADAS','Powertrain','Automation','User-triggered','System-triggered','Condition-based']::text[]),
('PF-0015', 'Driver Attention Monitoring', '驾驶员注意力监控', 'Monitors driver''s attention state and provides warnings for distraction or drowsiness via multiple interfaces.', '监控驾驶员注意力状态，通过多种界面提供分心或疲劳警告。', 'F005', ARRAY['safety','driving','ADAS','awareness','notification','monitoring','system-triggered','condition-based']::text[]),
('PF-0016', 'Steering Wheel Position Control', '方向盘位置控制', 'Controls the automatic return of the steering wheel to the zero position, typically in parking or remote control scenarios, ensuring correct wheel direction.', '控制方向盘自动回正至零位，通常在驻车或远程控制场景下，确保车轮方向正确。', 'F001', ARRAY['Convenience','Safety','Parking','Remote','Automation','ADAS','Body-control','Automation','System-triggered']::text[]),
('PF-0017', 'Bird View Camera Control', '全景影像控制', 'Control and configure settings for the Bird View 360 camera system, including enabling/disabling features like automatic perspective switching and moving object detection prompts.', '控制和配置360全景影像系统的设置，包括启用/禁用自动视角切换和移动物体检测提示等功能。', 'F001', ARRAY['Safety','Convenience','Driving','Parking','IVI','ADAS','Infotainment','Personalization','Awareness','Manual-Control','User-triggered']::text[]),
('PF-0018', 'Rear Cross Traffic Alert Control', '后方车辆穿行预警控制', 'Controls the enabling, disabling, and state restoration of the Rear Cross Traffic Alert system, which warns the driver of approaching vehicles from the sides when reversing.', '控制后方车辆穿行预警系统的启用、禁用和状态恢复，该系统在倒车时警告驾驶员侧面有车辆接近。', 'F008', ARRAY['safety','driving','parking','ADAS','awareness','system-triggered','condition-based']::text[]),
('PF-0019', 'Lane Keeping & Centering', '车道保持与居中', 'Manages lane detection, departure warnings, centering assistance, and emergency lane keeping functions to ensure the vehicle stays within its lane.', '管理车道识别、偏离预警、车道居中辅助及紧急车道保持功能，确保车辆在车道内行驶。', 'F011', ARRAY['safety','driving','automation','ADAS','awareness','system-triggered','condition-based']::text[]),
('PF-0020', 'ACC Activation & Settings', 'ACC激活与设置', 'Manages the driver''s ability to enable, disable, and configure the Adaptive Cruise Control system via various input methods (buttons, voice, menus) and handles the internal state machine transitions.', '管理驾驶员通过按键、语音或菜单启用、禁用及配置自适应巡航控制系统的操作，并处理系统内部的状态机转换。', 'F011', ARRAY['Comfort','Safety','Driving','IVI','Voice','Physical-button','ADAS','Automation','User-triggered']::text[]),
('PF-0021', 'Parking and Speed Sign Assistance', '泊车与交通标志辅助', 'Controls and manages parking assistance and traffic sign recognition functions, including automatic parking system and speed limit information.', '控制和管理泊车辅助和交通标志识别功能，包括自动泊车系统和限速信息。', 'F011', ARRAY['convenience','safety','driving','parking','ADAS','automation','awareness','system-triggered','condition-based','information']::text[]),
('PF-0022', 'Traffic Light Recognition Status Notification', '交通灯识别状态通知', 'Notifies the user about the status and any failures of the Traffic Light Recognition system, including self-check processes and fault triggers, via display or sound.', '通过显示或声音通知用户交通灯识别系统的状态和任何故障，包括自检过程和故障触发。', 'F045', ARRAY['safety','driving','IVI','ADAS','notification','system-triggered','awareness','feedback']::text[]),
('PF-0023', 'Surround View Display and Obstacle Alert', '全景影像显示与障碍物警报', 'Provides visual display of vehicle surroundings with obstacle detection and alerts to assist in parking and low-speed maneuvering.', '提供车辆周围环境的视觉显示，包括障碍物检测和警报，以辅助停车和低速操控。', 'F004', ARRAY['safety','convenience','parking','driving','ADAS','body-control','infotainment','awareness','feedback','notification','system-triggered','condition-based']::text[]),
('PF-0024', 'Seatbelt Safety Enhancement', '安全带安全增强', 'Enhances occupant safety by automatically tightening seatbelts during emergency situations to reduce injury risk.', '在紧急情况下自动收紧安全带，以减少乘员受伤风险，增强安全保护。', 'F015', ARRAY['safety','emergency','ADAS','body-control','automation','system-triggered','condition-based']::text[]),
('PF-0025', 'Adaptive High Beam Control', '自适应远近光灯控制', 'Control the adaptive high beam system, including turning it on/off and adjusting settings, to enhance nighttime driving visibility and safety.', '控制自适应远近光灯系统，包括开启/关闭和调整设置，以提升夜间驾驶的可见性和安全性。', 'F048', ARRAY['safety','driving','automation','ADAS','awareness','system-triggered','condition-based','lighting']::text[]),
('PF-0026', 'Driver Assistance Control in Energy Save Mode', '节能模式下的驾驶辅助控制', 'Allows users to manage driver assistance systems (e.g., lane warnings, blind spot monitoring) in Energy Save Mode, including manual activation and state restoration.', '允许用户在节能模式下管理驾驶辅助系统（如车道警告、盲点监测），包括手动激活和状态恢复。', 'F008', ARRAY['efficiency','safety','driving','ADAS','powertrain','manual-control','automation','user-triggered','system-triggered','condition-based']::text[]),
('PF-0027', 'Bird View Camera Status Notification', '全景影像系统状态通知', 'Provides notifications and alerts to the driver regarding the status, errors, or failures of the Bird View 360 Camera system.', '向驾驶员提供关于360全景影像系统状态、错误或故障的通知和警示。', 'F045', ARRAY['safety','awareness','notification','driving','parking','ADAS','body-control','system-triggered','condition-based']::text[]),
('PF-0028', 'Parking System Status Notification', '泊车系统状态通知', 'Provides notifications and alerts about the status of the automatic parking system, including activation failures, system readiness, and operational feedback.', '提供自动泊车系统的状态通知和警报，包括激活失败、系统准备情况和操作反馈。', 'F001', ARRAY['Convenience','Safety','Parking','ADAS','Notification','Feedback','System-triggered','Condition-based']::text[]),
('PF-0029', 'Digital Mirror Configuration', '数字后视镜配置', 'Allows users to configure settings and scenes for the digital interior mirror (DIM), such as display modes, brightness, or custom views.', '允许用户配置数字车内后视镜（DIM）的设置和场景，例如显示模式、亮度或自定义视图。', 'F008', ARRAY['Convenience','Safety','Driving','IVI','Body-Control','Personalization','Manual-Control','User-Triggered']::text[]),
('PF-0030', 'Suspicious Activity Detection', '可疑活动检测', 'System detects suspicious activities or events in the vehicle environment to enhance security and safety.', '系统检测车辆环境中的可疑活动或事件，以增强安全性和防护。', 'F001', ARRAY['security','safety','driving','parking','emergency','ADAS','body-control','security','awareness','notification','monitoring','system-triggered','event-based','condition-based']::text[]),
('PF-0031', 'Hill Hold Control Status Display', '坡道辅助控制状态显示', 'Displays the status of the Automatic Vehicle Hold (AVH) function, including active, standby, or fault states, to inform the driver about the system''s operational condition.', '显示自动驻车（AVH）功能的状态，包括激活、待机或故障状态，以告知驾驶员系统的运行状况。', 'F004', ARRAY['safety','driving','IVI','ADAS','feedback','monitoring','system-triggered','condition-based','information']::text[]),
('PF-0032', 'Steering Protection', '转向保护', 'Protects the steering system from damage by reducing torque at extreme angles to prevent mechanical stress.', '通过减少极端角度下的扭矩来保护转向系统免受损坏，防止机械应力。', 'F005', ARRAY['safety','driving','ADAS','body-control','system-triggered','condition-based']::text[]),
('PF-0033', 'Steering Protection Control', '转向保护控制', 'Controls steering system protection functions to prevent damage or unsafe conditions, such as power ramp-down during blocking scenarios.', '控制转向系统保护功能，以防止损坏或不安全情况，例如在阻塞情况下的功率斜坡下降。', 'F005', ARRAY['safety','driving','automation','ADAS','body-control','monitoring','system-triggered','condition-based']::text[]),
('PF-0034', 'Motor Thermal Management', '电机热管理', 'Controls and protects the motor system by managing temperature to prevent overheating and ensure safe operation.', '通过管理温度来控制并保护电机系统，防止过热，确保安全运行。', 'F002', ARRAY['safety','powertrain','monitoring','system-triggered','condition-based','efficiency']::text[]),
('PF-0035', 'Steering Assist Smoothing', '转向助力平顺控制', 'Controls the gradual increase or decrease of steering assist force during vehicle startup, shutdown, fault detection, or protection activation to prevent sudden changes in steering feel.', '在车辆启动、关闭、检测到故障或触发保护功能时，控制转向助力平缓上升或下降，避免转向手感突变。', 'F005', ARRAY['comfort','safety','driving','ADAS','body-control','automation','system-triggered','condition-based']::text[]),
('PF-0036', 'Braking Stability Assistance', '制动稳定性辅助', 'Provides assistance to maintain vehicle stability during braking, including anti-lock braking and related fault notifications.', '在制动时提供辅助以保持车辆稳定性，包括防抱死制动及相关故障通知。', 'F003', ARRAY['safety','driving','ADAS','awareness','notification','system-triggered','condition-based']::text[]),
('PF-0037', 'Brake System Status Monitoring', '制动系统状态监控', 'Monitors and alerts the driver about the status of brake system components, such as brake fluid level, to ensure safe operation.', '监控并提醒驾驶员制动系统组件（如制动液位）的状态，以确保安全操作。', 'F011', ARRAY['safety','driving','ADAS','body-control','awareness','notification','monitoring','system-triggered','condition-based']::text[]),
('PF-0038', 'Emergency Braking Assistance', '紧急制动辅助', 'Provides emergency braking functionality when normal braking systems are unavailable, such as through rear wheel anti-lock control or other backup braking methods.', '在常规制动系统不可用时提供紧急制动功能，例如通过后轮防抱死控制或其他备用制动方法。', 'F003', ARRAY['safety','emergency','ADAS','automation','system-triggered','condition-based','driving']::text[]),
('PF-0039', 'Maintenance Mode Control', '维修模式控制', 'Controls the activation and status of maintenance modes for vehicle systems, such as the Electronic Parking Brake (EPB), to facilitate servicing and repairs.', '控制车辆系统（如电子驻车制动EPB）维修模式的激活和状态，以便于维修和保养。', 'F008', ARRAY['convenience','maintenance','automation','body-control','manual-control','user-triggered','system-triggered']::text[]),
('PF-0040', 'Rollerbench Test Mode Control', '转毂台架测试模式控制', 'Controls the vehicle''s entry into and status of rollerbench test mode for diagnostic or testing purposes, typically used in maintenance or factory settings.', '控制车辆进入转毂台架测试模式及其状态，用于诊断或测试目的，通常在维护或工厂设置中使用。', 'F008', ARRAY['convenience','maintenance','automation','powertrain','body-control','manual-control','user-triggered']::text[]),
('PF-0041', 'Traction Control System Management', '牵引力控制系统管理', 'Manages the traction control system to improve vehicle stability and passability during acceleration or on slippery surfaces.', '管理牵引力控制系统，以提高车辆在加速或湿滑路面行驶时的稳定性和通过性。', 'F005', ARRAY['safety','driving','ADAS','powertrain','automation','system-triggered','condition-based']::text[]),
('PF-0042', 'Tire Pressure Monitoring Control', '胎压监测控制', 'Controls the monitoring and management of tire pressure in real-time while driving, including alerts and status display.', '控制车辆行驶时胎压的实时监测和管理，包括警报和状态显示。', 'F005', ARRAY['safety','driving','IVI','ADAS','body-control','monitoring','system-triggered','condition-based','notification','information']::text[]),
('PF-0043', 'Factory Mode Control', '工厂模式控制', 'Controls the factory mode for diagnostic and maintenance purposes, including entering/exiting the mode and managing system behavior during factory mode.', '控制用于诊断和维护的工厂模式，包括进入/退出模式以及在工厂模式下管理系统行为。', 'F005', ARRAY['maintenance','convenience','body-control','system-triggered','security','automation']::text[]),
('PF-0044', 'Hill Descent Control Management', '陡坡缓降控制管理', 'Allows users to control and manage the hill descent control function for safe and controlled descent on steep slopes.', '允许用户控制和管理陡坡缓降功能，以便在陡坡上安全、受控地下降。', 'F001', ARRAY['safety','driving','ADAS','automation','condition-based']::text[]),
('PF-0045', 'Vehicle Speed Information', '车速信息', 'Provides current vehicle speed information to the driver or other systems', '向驾驶员或其他系统提供当前车速信息', 'F001', ARRAY['safety','information','driving','ADAS','powertrain','body-control','infotainment','awareness','feedback','monitoring','system-triggered']::text[]),
('PF-0046', 'Steering Angle Monitoring', '转向角度监控', 'Monitors and calculates steering gear angle, angle speed, and signal validity to support steering-related vehicle functions.', '监控和计算转向齿轮角度、角度速度和信号有效性，以支持与转向相关的车辆功能。', 'F012', ARRAY['Safety','Driving','ADAS','Monitoring','Condition-based']::text[]),
('PF-0047', 'Parking Brake Status Notification', '驻车制动状态通知', 'Provides visual or other notifications about the status of the parking brake system, including failures or operational states, to inform the driver.', '提供关于驻车制动系统状态的视觉或其他通知，包括故障或操作状态，以告知驾驶员。', 'F039', ARRAY['safety','information','parking','IVI','body-control','ADAS','notification','system-triggered','condition-based']::text[]),
('PF-0048', 'Automatic Vehicle Hold and Steering Profile Management', '自动驻车和转向配置文件管理', 'Manages and stores automatic vehicle hold and steering-related settings in user profiles for personalized driving experience.', '在用户配置文件中管理和存储自动驻车及转向相关设置，实现个性化驾驶体验。', 'F010', ARRAY['Convenience','Personalization','Driving','Parking','ADAS','Body-Control','Automation','User-Triggered']::text[]),
('PF-0049', 'Towing Mode Management', '拖车模式管理', 'Controls and manages the activation and deactivation of towing mode for vehicle towing operations.', '控制和管理拖车模式的激活和停用，用于车辆拖车操作。', 'F008', ARRAY['safety','driving','body-control','powertrain','automation','user-triggered','condition-based']::text[]),
('PF-0050', 'Makeup Mirror Lighting Control', '化妆镜灯光控制', 'Controls the automatic lighting of makeup mirrors for driver and passenger when opened or closed.', '控制主/副驾驶化妆镜在打开或关闭时的自动灯光功能。', 'F003', ARRAY['Convenience','Lighting','Body-control','Automation','Event-based','System-triggered']::text[]),
('PF-0051', 'Window Actuation & State', '车窗执行与状态', 'Manages the physical actuation of window motors and provides real-time window position status.', '管理车窗电机的物理执行动作，并提供实时的车窗位置状态信息。', 'F037', ARRAY['Comfort','Convenience','Safety','Driving','Parking','Remote','Physical-button','Automation','Body-control','Security','Manual-control','Monitoring','User-triggered','System-triggered']::text[]),
('PF-0052', 'Low Speed Pedestrian Alert Control', '低速行人提醒控制', 'Control of acoustic alerts to warn pedestrians and other traffic participants when the vehicle is moving at low speeds, typically for electric vehicles to comply with safety regulations.', '控制车辆在低速行驶时发出声音提示，以警告行人和其他交通参与者，通常用于电动汽车以符合安全法规。', 'F013', ARRAY['safety','driving','ADAS','awareness','system-triggered','condition-based','audio']::text[]),
('PF-0053', 'Digital Key Zone Management', '数字钥匙区域管理', 'Manages the determination and control of digital key zones based on signal strength, enabling secure vehicle access and operation within defined areas.', '基于信号强度确定和控制数字钥匙区域，实现在定义区域内安全访问和操作车辆。', 'F001', ARRAY['security','convenience','parking','mobile-app','key-fob','security','body-control','automation','location-based','condition-based']::text[]),
('PF-0054', 'Exterior Mirror Folding Control', '外后视镜折叠控制', 'Controls the folding and unfolding of exterior mirrors through manual user operations or automatic system triggers, enhancing convenience and safety.', '通过用户手动操作或系统自动触发控制外后视镜的折叠和展开，以提高便利性和安全性。', 'F011', ARRAY['convenience','safety','parking','physical-button','automation','body-control','manual-control','user-triggered','system-triggered']::text[]),
('PF-0055', 'Horn Mode Configuration', '喇叭模式配置', 'Allows users to configure horn modes, such as enabling or disabling automatic low-tone activation, via steering wheel panel or mobile app, typically for scenarios like key inside vehicle, anti-theft alarm, or door open reminders.', '允许用户通过方向盘面板或手机APP配置喇叭模式，例如启用或禁用低音自动激活功能，通常用于钥匙在车内、防盗报警或车门未关提醒等场景。', 'F019', ARRAY['convenience','security','parking','remote','mobile-app','physical-button','body-control','security','personalization','notification','user-triggered','event-based','condition-based']::text[]),
('PF-0056', 'Charging Status Display', '充电状态显示', 'Displays the status of the vehicle''s charging system, including charging flap open/closed status, to inform the user about the charging process.', '显示车辆充电系统的状态，包括充电盖板开/关状态，以告知用户充电过程。', 'F005', ARRAY['information','charging','charging','feedback','monitoring']::text[]),
('PF-0057', 'Airbag Safety Control', '安全气囊安全控制', 'Control and management of airbag settings for passenger safety, including enabling/disabling front passenger airbag.', '控制和管理安全气囊设置以保障乘客安全，包括开启/关闭副驾安全气囊。', 'F015', ARRAY['safety','driving','body-control','automation','condition-based','user-triggered']::text[]),
('PF-0058', 'Hood Control', '发动机罩控制', 'Controls the opening, closing, and locking of the vehicle''s hood, including manual and automated operations.', '控制车辆发动机罩的打开、关闭和锁定，包括手动和自动操作。', 'F019', ARRAY['Convenience','Safety','Maintenance','Body-control','Automation','Manual-control','User-triggered','System-triggered']::text[]),
('PF-0059', 'Wiper Maintenance Mode Control', '雨刮器维护模式控制', 'Controls the activation and management of wiper maintenance modes, such as moving wiper arms to lifting position for service or replacement.', '控制雨刮器维护模式的激活和管理，例如将雨刮臂移动到抬升位置以便维修或更换。', 'F014', ARRAY['convenience','maintenance','body-control','manual-control','user-triggered']::text[]),
('PF-0060', 'Door Handle Unfreeze Control', '门把手破冰控制', 'Controls the function to unfreeze hidden door handles by increasing output force when stuck due to ice or other obstructions.', '控制隐藏式门把手在冰冻或其他障碍物卡住时，通过增加输出力来破冰或解除卡滞的功能。', 'F008', ARRAY['Convenience','Body-control','Condition-based','Automation','Security','Winter']::text[]),
('PF-0061', 'Hazard Warning Control', '危险报警控制', 'Controls the activation and deactivation of hazard warning lights through various interfaces such as IVI, mobile app, or physical button.', '通过IVI、手机APP或物理按钮等多种接口控制危险报警灯的开启和关闭。', 'F022', ARRAY['safety','emergency','IVI','mobile-app','physical-button','lighting','body-control','manual-control','user-triggered']::text[]),
('PF-0062', 'Central Lock Control', '中控锁控制', 'Control and configure central locking functions, including automatic locking, door state management, and user configuration.', '控制和配置中控锁功能，包括自动锁门、车门状态管理和用户配置。', 'F020', ARRAY['Safety','Convenience','Security','Driving','Parking','Automation','Physical-button','Key-fob','Body-control','Security','Personalization','Automation','User-triggered','System-triggered','Condition-based']::text[]),
('PF-0063', 'Seat System Diagnostics', '座椅系统诊断', 'Provides status notifications and alerts for seat system malfunctions, such as motor faults or adjustment issues, to inform users of potential problems.', '提供座椅系统故障的状态通知和警报，例如电机故障或调节问题，以告知用户潜在问题。', 'F045', ARRAY['convenience','information','driving','body-control','experience_type:monitoring','experience_type:notification','system-triggered','condition-based']::text[]),
('PF-0064', 'Passenger Role Management', '乘客角色管理', 'Allows configuration and management of passenger roles and policies, particularly for specialized vehicle modes like taxi services.', '允许配置和管理乘客角色及策略，特别适用于出租车等专业车辆模式。', 'F013', ARRAY['Convenience','Security','Car-sharing','Infotainment','Telematics','Personalization','User-triggered','System-triggered']::text[]),
('PF-0065', 'Door Status Monitoring', '车门状态监控', 'Monitors and detects the open/closed status of all vehicle doors including the tailgate, providing real-time status information for safety and convenience features.', '监控和检测所有车门（包括尾门）的开关状态，为安全和便利功能提供实时状态信息。', 'F033', ARRAY['safety','convenience','driving','parking','body-control','security','awareness','notification','monitoring','event-based','condition-based']::text[]),
('PF-0066', 'Passenger Occupancy Detection', '乘客占用检测', 'Detects whether passenger seats (e.g., front, rear right, rear left) are occupied to support safety, comfort, or system functionality.', '检测乘客座椅（如前排、后排右、后排左）是否被占用，以支持安全、舒适或系统功能。', 'F001', ARRAY['safety','comfort','driving','ADAS','body-control','monitoring','condition-based','system-triggered']::text[]),
('PF-0067', 'Wash Fluid Level Monitoring', '清洗液液位监测', 'Monitors and displays the status of windshield washer fluid levels to inform the driver when refill is needed.', '监测并显示挡风玻璃清洗液液位状态，在需要补充时通知驾驶员。', 'F048', ARRAY['safety','convenience','driving','body-control','monitoring','condition-based','notification']::text[]),
('PF-0068', 'Exterior Lighting Control', '外部灯光控制', 'Control and manage the activation and deactivation of exterior vehicle lights, such as position lamps, headlights, and other external lighting features, to enhance visibility and safety.', '控制和管理车辆外部灯光的开启和关闭，例如位置灯、前大灯等外部照明功能，以提高可见性和安全性。', 'F001', ARRAY['safety','driving','parking','automation','physical-button','sensor-triggered','lighting','body-control','awareness','automation','user-triggered','system-triggered','condition-based']::text[]),
('PF-0069', 'Taxi Arrival Lighting Control', '出租车到达照明控制', 'Controls the taxi arrival illumination feature, including enabling/disabling automatic activation upon arrival via mobile app or IVI.', '控制出租车到达照明功能，包括通过手机应用或车载信息娱乐系统启用/禁用到达时自动激活。', 'F028', ARRAY['Convenience','Lighting','Mobile-App','IVI','Automation','Location-based','User-triggered','Body-control']::text[]),
('PF-0070', 'Fog Light Control', '雾灯控制', 'Controls front and rear fog lights, including automatic activation, voice control, and passenger restrictions.', '控制前雾灯和后雾灯，包括自动开启、语音控制和乘客限制功能。', 'F003', ARRAY['safety','driving','voice','automation','physical-button','lighting','body-control','automation','user-triggered','condition-based']::text[]),
('PF-0071', 'Interior Lighting Control', '车内照明控制', 'Controls the activation, deactivation, and brightness settings of interior lights such as dome lamps, roof lamps, and other cabin lighting fixtures to enhance visibility and ambiance.', '控制车内照明（如顶灯、阅读灯等）的开关和亮度设置，以提升车内可见度和氛围。', 'F013', ARRAY['comfort','convenience','safety','driving','parking','physical-button','automation','body-control','lighting','personalization','manual-control','user-triggered','system-triggered']::text[]),
('PF-0072', 'Interior Lighting Control via Mobile', '通过移动应用控制车内照明', 'Allows users to control interior lighting settings (e.g., roof/dome lamp brightness) through a mobile application, enhancing convenience and personalization.', '允许用户通过移动应用程序控制车内照明设置（例如，车顶/顶灯亮度），提升便利性和个性化体验。', 'F013', ARRAY['Convenience','Personalization','Mobile-App','Lighting','Manual-Control','User-Triggered']::text[]),
('PF-0073', 'Automatic Emergency Braking', '自动紧急制动', 'Controls automatic emergency braking functions including AEB activation, system state management, failure notifications, and related braking assistance.', '控制自动紧急制动功能，包括AEB激活、系统状态管理、故障通知和相关制动辅助。', 'F011', ARRAY['safety','driving','automation','ADAS','awareness','notification','system-triggered','condition-based','emergency']::text[]),
('PF-0074', 'Object Detection and Collision Handling', '目标物检测与碰撞处理', 'Handles object recognition, collision detection, and related functions such as rear cross traffic alert and traffic alert.', '处理目标物识别、碰撞检测及相关功能，如后方车辆穿行预警和交通警报。', 'F011', ARRAY['safety','driving','ADAS','awareness','system-triggered','condition-based']::text[]),
('PF-0075', 'Driver Seat Position Adjustment', '驾驶员座椅位置调节', 'Control and manage the adjustment of driver seat position through multiple interfaces (steering wheel panel, voice assistant, mobile app), with safety restrictions based on vehicle mode and door status.', '通过多种界面（方向盘面板、语音助手、移动应用）控制和管理驾驶员座椅位置调节，并根据车辆模式和车门状态实施安全限制。', 'F001', ARRAY['Comfort','Safety','Driving','Parking','Remote','Mobile-app','Voice','Physical-button','Body-control','Personalization','Manual-control','User-triggered','Condition-based']::text[]),
('PF-0076', 'Passenger Seat Position Adjustment', '副驾座椅位置调节', 'Control and adjust passenger seat position via multiple interfaces including steering wheel panel, buttons, mobile app, and voice assistant, with status display and restrictions.', '通过方向盘面板、按钮、移动应用和语音助手控制和调节副驾座椅位置，包括状态显示和限制条件。', 'F001', ARRAY['comfort','convenience','driving','parking','voice','mobile-app','physical-button','body-control','personalization','manual-control','user-triggered']::text[]),
('PF-0077', 'Driving Automation (DA) Management', '驾驶自动化（DA）管理', 'Handles the activation, deactivation, mode switching, and status control for Driving Automation functions, including longitudinal and lateral movement control and driver notifications.', '处理驾驶自动化功能的激活、退出、模式切换和状态控制，包括纵向和横向移动控制及驾驶员通知。', 'F011', ARRAY['Safety','Convenience','Driving','Automation','ADAS','Automation','Notification','System-triggered','Condition-based']::text[]),
('PF-0078', 'Driver Takeover and Safety Notifications', '驾驶员接管与安全通知', 'Manages alerts and notifications to prompt driver takeover in automated driving scenarios, including hands-off warnings, override controls, and system reactivation prompts.', '管理在自动驾驶场景中提示驾驶员接管的警报和通知，包括脱手警告、超控控制和系统重新激活提示。', 'F011', ARRAY['safety','driving','ADAS','notification','system-triggered','awareness']::text[]),
('PF-0079', 'Auxiliary Driving and Control Functions', '辅助驾驶与控制功能', 'Includes supplementary driving assistance features such as parking control, hill descent control, lane centering, and steering assistance, integrated with ACC/DA systems.', '包括泊车控制、陡坡缓降控制、车道居中保持和转向辅助等补充驾驶辅助功能，与ACC/DA系统集成。', 'F011', ARRAY['Safety','Convenience','Driving','Parking','ADAS','Body-Control','Automation','Awareness','System-Triggered','Condition-Based']::text[]),
('PF-0080', 'Steering Wheel Illumination Control', '方向盘照明控制', 'Configure and manage the illumination settings for steering wheel buttons, including brightness and automatic brightness control, stored in driver profiles.', '配置和管理方向盘按钮的照明设置，包括亮度和自动亮度控制，存储在驾驶员配置文件中。', 'F028', ARRAY['comfort','safety','driving','lighting','body-control','personalization','user-triggered','convenience']::text[]),
('PF-0081', 'Windshield Washer Control', '前挡风玻璃清洗控制', 'Controls the activation and operation of the front windshield washer system for cleaning the windshield.', '控制前挡风玻璃清洗系统的激活和操作，用于清洁挡风玻璃。', 'F012', ARRAY['safety','driving','physical-button','body-control','manual-control','user-triggered','convenience','awareness']::text[]),
('PF-0082', 'Rear Camera Washer Control', '后摄像头清洗控制', 'Controls the activation and deactivation of the rear camera washer to maintain camera clarity.', '控制后摄像头清洗器的激活和停用，以保持摄像头清晰度。', 'F013', ARRAY['safety','convenience','driving','ADAS','body-control','automation','condition-based','system-triggered']::text[]),
('PF-0083', 'Rear Seat Heating Status Display via Mobile', '后排座椅加热状态手机显示', 'Allows users to view the heating status (e.g., on/off, temperature level) of rear seats through a mobile application, enhancing remote comfort monitoring.', '允许用户通过手机应用查看后排座椅的加热状态（如开关状态、温度级别），提升远程舒适度监控。', 'F001', ARRAY['comfort','remote','mobile-app','climate','monitoring','user-triggered','convenience','information']::text[]),
('PF-0084', 'Steering Wheel Heating Control', '方向盘加热控制', 'Controls the heating function of the steering wheel to enhance driver comfort in cold conditions.', '控制方向盘加热功能，以在寒冷条件下提升驾驶员舒适度。', 'F002', ARRAY['comfort','driving','physical-button','climate','manual-control','user-triggered','condition-based']::text[]),
('PF-0085', 'Tailgate Window Heating Control', '尾门车窗加热控制', 'Allows users to control the heating function of the tailgate window, including toggling on/off via mobile application, to enhance visibility and safety in cold conditions.', '允许用户控制尾门车窗的加热功能，包括通过移动应用程序开关，以在寒冷条件下提高能见度和安全性。', 'F002', ARRAY['safety','convenience','remote','mobile-app','body-control','climate','manual-control','user-triggered','condition-based']::text[]),
('PF-0086', 'Wireless Charger Management', '无线充电器管理', 'Manage the wireless charging functionality for devices in the vehicle, including detecting device presence and controlling charging status.', '管理车辆内设备的无线充电功能，包括检测设备存在和控制充电状态。', 'F016', ARRAY['Convenience','Charging','Charging','Automation','System-triggered','Monitoring']::text[]),
('PF-0087', 'Tailgate Lock Control', '尾门锁控制', 'Controls the locking and unlocking of the tailgate, including manual toggle and automatic activation based on user requests or vehicle conditions.', '控制尾门的锁定和解锁，包括手动切换和基于用户请求或车辆条件的自动激活。', 'F019', ARRAY['Convenience','Security','Body-control','Physical-button','Key-fob','Automation','User-triggered','System-triggered','Manual-control']::text[]),
('PF-0088', 'Charging Lid Lock Control', '充电盖锁控制', 'Controls the locking and unlocking of the charging lid, allowing users to secure or access the charging port.', '控制充电盖的锁定和解锁，允许用户保护或访问充电端口。', 'F019', ARRAY['convenience','security','charging','body-control','user-triggered','manual-control']::text[]),
('PF-0089', 'Thermal & Lighting Comfort in Energy Save Mode', '节能模式下的热管理与灯光舒适性', 'Manages high-power consumption heating elements (seats, windows, mirrors) and ambient lighting features during Energy Save Mode, including manual activation and deactivation.', '管理节能模式下的高功耗加热元件（座椅、车窗、后视镜）和氛围灯功能，包括手动激活和停用。', 'F008', ARRAY['Comfort','Efficiency','Driving','Idle','Physical-button','Automation','Climate','Lighting','Body-control','Efficiency','Manual-control','User-triggered','System-triggered','Condition-based']::text[]),
('PF-0090', 'Exterior Lighting Control in Energy Save Mode', '节能模式下的外部照明控制', 'Allows users to manage exterior lighting features (e.g., roof lamp, puddle lights) in Energy Save Mode, including manual activation and deactivation.', '允许用户在节能模式下管理外部照明功能（如顶灯、迎宾灯），包括手动激活和停用。', 'F008', ARRAY['Convenience','Efficiency','Parking','Remote','Mobile-app','Automation','Physical-button','Lighting','Body-control','Efficiency','Manual-control','User-triggered','System-triggered']::text[]),
('PF-0091', 'Carsharing Booking Activation', '共享汽车预订激活', 'Manages the automatic activation of the booking process for carsharing vehicles upon receiving valid requests from telematics systems.', '管理共享汽车在收到来自远程信息处理系统的有效请求时自动激活预订流程。', 'F041', ARRAY['Convenience','Car-sharing','Automation','Telematics','Automation','System-triggered','Event-based']::text[]),
('PF-0092', 'Wellness Animation Configuration', '健康动画配置', 'Configure and manage wellness-themed animations (e.g., smooth breathing) for different vehicle modes or scenarios, such as Taxi, Family, and Delivery, to enhance passenger comfort and ambiance.', '为不同车辆模式或场景（如出租车、家庭和送货）配置和管理健康主题动画（例如平滑呼吸），以提升乘客舒适度和氛围。', 'F041', ARRAY['wellness','comfort','infotainment','body-control','personalization','automation','condition-based','driving','idle']::text[]),
('PF-0093', 'Child Lock Activation and Control', '儿童锁激活与控制', 'Provides multiple methods to activate, deactivate, and toggle child lock settings for rear doors, including via steering wheel panel, mobile app, voice commands, and automatic activation features.', '提供多种激活、停用和切换后门儿童锁设置的方法，包括通过方向盘面板、手机应用、语音命令和自动激活功能。', 'F019', ARRAY['safety','driving','mobile-app','voice','automation','physical-button','body-control','security','manual-control','user-triggered','system-triggered']::text[]),
('PF-0094', 'Window Control Indication', '车窗控制指示', 'Provides visual indication and control for window selection and operation, including default driver window selection and timed reset features.', '提供车窗选择和操作的视觉指示与控制，包括默认驾驶员车窗选择和定时重置功能。', 'F003', ARRAY['Convenience','Safety','Driving','Body-Control','Feedback','Manual-Control','User-Triggered','Time-Based','Physical-Button']::text[]),
('PF-0095', 'Window Lock Control', '车窗锁控制', 'Controls the window lock function to prevent rear passengers from operating windows, and provides status feedback.', '控制车窗锁功能以防止后排乘客操作车窗，并提供状态反馈。', 'F011', ARRAY['safety','body-control','driving','physical-button','feedback','manual-control','user-triggered']::text[]),
('PF-0096', 'Turn Signal Control', '转向灯控制', 'Manages the operation and safety features of turn signals, including preventing simultaneous flashing on both sides.', '管理转向灯的操作和安全功能，包括防止两侧同时闪烁。', 'F001', ARRAY['safety','navigation','driving','body-control','manual-control','user-triggered']::text[]),
('PF-0097', 'Exterior Lighting Animation Control', '外部灯光动画控制', 'Controls decorative lighting animations on the vehicle exterior, such as breathing effects, with configurable colors and modes, typically used for welcome or farewell lighting sequences.', '控制车辆外部装饰性灯光动画，例如呼吸效果，可配置颜色和模式，通常用于迎宾或送别灯光序列。', 'F019', ARRAY['Comfort','Convenience','Post-trip','Pre-trip','Lighting','Body-control','Personalization','User-triggered','System-triggered','Event-based']::text[]),
('PF-0098', 'Interior Lighting Control via Vehicle Lock/Unlock', '通过车辆锁定/解锁控制内饰照明', 'Controls the automatic activation of interior backlighting (e.g., USB ports, gear shift indicators, SOS buttons, window controls) based on vehicle lock/unlock status, without brightness adjustment.', '基于车辆锁定/解锁状态自动控制内饰背光（如USB接口、档位指示器、SOS按钮、车窗控制按钮）的激活，无亮度调节功能。', 'F019', ARRAY['Convenience','Lighting','Body-control','Automation','Event-based','Post-trip','Pre-trip','Security']::text[]),
('PF-0099', 'Window Rain Detection Alert', '车窗雨感提示', 'Provides alerts and prompts for window operations based on rain detection, such as automatically suggesting window closure when rain is detected.', '基于雨感检测提供车窗操作的提示和警报，例如检测到下雨时自动提示关闭车窗。', 'F039', ARRAY['Convenience','Safety','Driving','Automation','Body-control','Sensor-triggered','Condition-based','Notification']::text[]),
('PF-0100', 'Door Latch Self-Priming Control', '门锁自启动控制', 'Controls the self-priming function of door latches (front left, front right, rear left, rear right) to ensure proper door operation and safety.', '控制门锁（左前、右前、左后、右后）的自启动功能，以确保车门正常操作和安全。', 'F001', ARRAY['safety','convenience','body-control','automation','system-triggered','condition-based']::text[]),
('PF-0101', 'Remote Vehicle Component Activation', '远程车辆组件激活', 'Controls the activation of vehicle components in response to remote commands, enabling users to remotely prepare or operate vehicle systems.', '控制车辆组件在接收到远程命令时的激活，使用户能够远程准备或操作车辆系统。', 'F001', ARRAY['Convenience','Remote','Mobile-App','Telematics','Body-Control','Manual-Control','User-Triggered','Pre-trip','Security']::text[]),
('PF-0102', 'Front Door Manual and Physical Activation', '前门手动与物理激活', 'Provides manual and physical methods to activate and control front door opening/closing, including handles, buttons, and direct manipulation.', '提供手动和物理方法激活和控制前门开关，包括门把手、按钮和直接操作。', 'F011', ARRAY['Convenience','Security','Body-control','Manual-control','User-triggered','Physical-button']::text[]),
('PF-0103', 'Interior Lighting Control via Voice', '语音控制车内照明', 'Controls interior lighting functions such as roof lamps, dome lights, and other cabin illumination through voice commands, allowing users to adjust brightness or activate/deactivate lights hands-free.', '通过语音命令控制车内照明功能，如顶灯、氛围灯等，允许用户免提调节亮度或开关灯光。', 'F006', ARRAY['Convenience','Comfort','Voice','Lighting','Manual-control','User-triggered','Driving']::text[]),
('PF-0104', 'AVAS Sound Preview', 'AVAS提示音预览', 'Allows users to preview AVAS (Acoustic Vehicle Alerting System) sample sounds during configuration before applying them, enhancing the customization experience for electric vehicle alert sounds.', '允许用户在配置过程中预览AVAS（电动汽车低速行驶提示音系统）示例声音，以便在应用前试听，提升电动汽车提示音的自定义体验。', 'F028', ARRAY['personalization','awareness','user-triggered','infotainment','audio','convenience','driving']::text[]),
('PF-0105', 'Vehicle Tilt Detection', '车辆倾斜检测', 'Detects and monitors the tilt angle of the vehicle using sensors like IMU or actuators, typically for stability, safety, or terrain adaptation purposes.', '使用IMU等传感器或执行器检测和监控车辆的倾斜角度，通常用于稳定性、安全性或地形适应目的。', 'F001', ARRAY['safety','driving','ADAS','body-control','monitoring','condition-based']::text[]),
('PF-0106', 'V2L Discharging Lighting Control', 'V2L放电照明控制', 'Controls the lighting system during vehicle-to-load (V2L) discharging operations, including activation and deactivation of lights based on discharging status.', '控制车辆对外放电（V2L）期间的照明系统，包括根据放电状态启动和停止灯光。', 'F003', ARRAY['convenience','lighting','charging','automation','system-triggered','condition-based']::text[]),
('PF-0107', 'Vehicle Evacuation Detection', '车辆撤离检测', 'Detects vehicle evacuation scenarios such as lifting, towing, or jacking up of the car, typically for safety or anti-theft purposes.', '检测车辆撤离场景，如车辆被抬起、拖走或顶起，通常用于安全或防盗目的。', 'F036', ARRAY['security','parking','body-control','security','awareness','event-based']::text[]),
('PF-0108', 'Exterior Mirror Position Adjustment', '外后视镜位置调节', 'Enables adjustment of exterior mirror positions via various interfaces and displays the adjustment state.', '通过多种界面调节外后视镜位置，并显示调节状态。', 'F011', ARRAY['Safety','Convenience','Driving','Parking','IVI','Physical-button','Body-control','Manual-control','User-triggered']::text[]),
('PF-0109', 'Exterior Mirror Heating Control', '外后视镜加热控制', 'Manages the activation, deactivation, and status display of exterior mirror heating for defogging and defrosting.', '管理外后视镜加热的激活、停用和状态显示，用于除雾和除霜。', 'F011', ARRAY['safety','convenience','driving','body-control','climate','automation','manual-control','user-triggered','condition-based']::text[]),
('PF-0110', 'Exterior Mirror Warning Lamp Control', '外后视镜警示灯控制', 'Controls the exterior mirror warning lamp for safety alerts.', '控制外后视镜警示灯，用于安全警示。', 'F011', ARRAY['safety','driving','ADAS','awareness','system-triggered','body-control','lighting']::text[]),
('PF-0111', 'Alarm System Management', '警报系统管理', 'Manage alarm system activation, deactivation, configuration, and state notifications based on security events.', '基于安全事件管理警报系统的激活、停用、配置和状态通知。', 'F020', ARRAY['security','remote','mobile-app','automation','security','notification','monitoring','user-triggered','event-based','condition-based']::text[]),
('PF-0112', 'Anti-Theft State Control', '防盗状态控制', 'Control and display the overall anti-theft state, including immobilizer functions and user interactions.', '控制和显示整体防盗状态，包括动力系统防盗功能和用户交互。', 'F020', ARRAY['security','parking','remote','mobile-app','key-fob','security','feedback','notification','user-triggered','system-triggered']::text[]),
('PF-0113', 'Automatic and Ambient Lighting', '自动和环境照明', 'Manages automatic lighting features (auto light, daytime running lights), ambient lighting, and interior roof lamp control.', '管理自动照明功能（自动灯、昼间行驶灯）、环境照明和车内顶灯控制。', 'F003', ARRAY['Comfort','Safety','Driving','Parking','Automation','Lighting','Body-Control','Personalization','Automation','Sensor-triggered','Condition-based']::text[]),
('PF-0114', 'Welcome and Approach Lighting', '欢迎和接近照明', 'Controls lighting features for user approach and departure, including follow me home, welcome lighting, approach lighting, and puddle lights.', '控制用户接近和离开时的照明功能，包括伴我回家、欢迎照明、接近照明和水坑灯。', 'F003', ARRAY['Comfort','Safety','Convenience','Post-trip','Pre-trip','Key-fob','Automation','Sensor-triggered','Lighting','Body-control','Automation','User-triggered','Event-based','Condition-based']::text[]),
('PF-0115', 'Rear and Signal Lighting', '后部和信号灯照明', 'Manages rear lighting (trunk lights, reverse lights, license plate lights, stop lamps) and signal indicators (direction indicators).', '管理后部照明（后备箱灯、倒车灯、牌照灯、制动灯）和信号指示灯（转向灯）。', 'F003', ARRAY['safety','driving','parking','body-control','lighting','awareness','feedback','system-triggered','condition-based','user-triggered']::text[]),
('PF-0116', 'Lighting Status and UI Control', '照明状态和用户界面控制', 'Handles lighting status displays (via SWP, mobile app), malfunction detection, and user interface controls like light menu access.', '处理照明状态显示（通过方向盘面板、移动应用）、故障检测和用户界面控制（如灯光菜单访问）。', 'F003', ARRAY['Convenience','Information','Lighting','Body-Control','Mobile-App','User-Triggered','Monitoring','Feedback']::text[]),
('PF-0117', 'Driver Seat Heating Control', '驾驶员座椅加热控制', 'Manage driver seat heating functions including activation, deactivation, status display, and automatic features via steering wheel panel, mobile app, and voice control.', '管理驾驶员座椅加热功能，包括通过方向盘面板、移动应用和语音控制进行激活、停用、状态显示和自动功能。', 'F001', ARRAY['Comfort','Driving','Mobile-App','Voice','Physical-Button','Automation','Climate','Body-Control','Personalization','Manual-Control','User-Triggered']::text[]),
('PF-0118', 'Driver Easy Access/Exit', '驾驶员轻松上下车', 'Configure and activate easy access and exit functions for the driver seat, including automatic adjustments and profile storage.', '配置和激活驾驶员座椅的轻松上下车功能，包括自动调节和配置文件存储。', 'F001', ARRAY['convenience','comfort','post-trip','pre-trip','body-control','personalization','automation','user-triggered','system-triggered']::text[]),
('PF-0119', 'Rear Seats Heating Control', '后排座椅加热控制', 'Control rear seats heating functions including activation, deactivation, state provision, and automatic features via steering wheel panel and mobile app.', '控制后排座椅加热功能，包括通过方向盘面板和移动应用进行激活、停用、状态提供和自动功能。', 'F001', ARRAY['comfort','convenience','driving','remote','mobile-app','physical-button','climate','body-control','automation','manual-control','user-triggered']::text[]),
('PF-0120', 'Passenger Seat Heating Control', '副驾座椅加热控制', 'Manage front passenger seat heating state through steering wheel panel, voice assistant, and mobile app, including auto-deactivation and status notification.', '通过方向盘面板、语音助手和移动应用管理副驾座椅加热状态，包括自动停用和状态通知。', 'F001', ARRAY['Comfort','Convenience','Driving','Voice','Mobile-App','Automation','Physical-Button','Climate','Body-Control','Personalization','Notification','Automation','User-Triggered','Condition-Based']::text[]),
('PF-0121', 'Passenger Easy Access/Exit Configuration', '副驾轻松上下车配置', 'Configure and control passenger easy access/exit features via steering wheel panel and mobile app, including auto-activation and setting storage.', '通过方向盘面板和移动应用配置和控制副驾轻松上下车功能，包括自动激活和设置存储。', 'F001', ARRAY['Convenience','Comfort','Parking','Mobile-App','Body-Control','Personalization','User-Triggered','Automation']::text[]),
('PF-0122', 'Passenger Seat Adjustment Restrictions', '副驾座椅调节限制', 'Handle restrictions and deactivation notifications for passenger seat adjustment, including specific vehicle configurations and driving conditions.', '处理副驾座椅调节的限制和停用通知，包括特定车型配置和驾驶条件。', 'F001', ARRAY['Safety','Comfort','Driving','Body-control','ADAS','Notification','Condition-based','System-triggered']::text[]),
('PF-0123', 'Child Lock Status Display and Configuration', '儿童锁状态显示与配置', 'Displays child lock status on steering wheel panel and mobile app, and allows configuration of related door lock features such as walk-away lock and driver door only lock.', '在方向盘面板和手机应用上显示儿童锁状态，并允许配置相关门锁功能，如离车自动上锁和仅驾驶员门锁。', 'F019', ARRAY['safety','convenience','driving','mobile-app','body-control','infotainment','security','awareness','feedback','user-triggered','monitoring']::text[]),
('PF-0124', 'Rear Door Access and Locking Restrictions', '后门访问与锁定限制', 'Manages restrictions on opening and closing rear doors when child lock is active, including disabling internal door buttons and handling rear door opening requests.', '管理儿童锁激活时后门的打开和关闭限制，包括禁用内部门按钮和处理后门打开请求。', 'F019', ARRAY['Safety','Security','Driving','Body-Control','Automation','Condition-based','User-triggered']::text[]),
('PF-0125', 'Central Locking & Physical Control', '中央门锁与物理控制', 'Controls vehicle door locking/unlocking via physical buttons (SWP, door switches), voice commands, and remote control. Manages latch actuation, status feedback, and failure notifications.', '通过物理按钮（方向盘面板、车门开关）、语音指令和遥控器控制车门锁止/解锁。管理锁扣动作、状态反馈及失败通知。', 'F019', ARRAY['Convenience','Security','Remote','Physical-button','Voice','Key-fob','Body-control','Feedback','Notification','User-triggered']::text[]),
('PF-0126', 'Rear Door Activation and Control', '后门激活与控制', 'Manages the activation and control of rear doors through manual, automatic, and remote methods, including configuration and operation via various interfaces.', '管理后门的激活和控制，通过手动、自动和远程方法，包括通过多种接口的配置和操作。', 'F011', ARRAY['Convenience','Body-Control','Remote','Mobile-App','Physical-Button','Key-Fob','Automation','User-Triggered','System-Triggered','Manual-Control']::text[]),
('PF-0127', 'Door Configuration and Settings', '车门配置与设置', 'Allows users to configure and customize door settings, including parameters, reminders, and voice control options for front doors.', '允许用户配置和自定义车门设置，包括前门的参数、提醒和语音控制选项。', 'F011', ARRAY['Convenience','Personalization','Voice','Body-Control','User-Triggered','Automation']::text[]),
('PF-0128', 'Door Safety and Obstacle Handling', '车门安全与障碍物处理', 'Ensures safe door operation by handling obstacle detection, collision prevention, pinch events, and related notifications during opening and closing.', '通过处理障碍物检测、碰撞预防、夹持事件以及开关过程中的相关通知，确保车门操作安全。', 'F011', ARRAY['safety','body-control','awareness','notification','system-triggered','event-based']::text[]),
('PF-0129', 'Legacy and Deprecated Functions', '遗留与废弃功能', 'Includes functions that are planned for deletion or are no longer actively maintained, such as handle control and activation status features.', '包含计划删除或不再积极维护的功能，例如把手控制和激活状态功能。', 'F011', ARRAY['Maintenance','Body-control','Manual-control','System-triggered']::text[]),
('PF-0130', 'Gate Control', '闸门控制', 'Controls for opening and closing gates, typically via vehicle interfaces like the steering wheel panel.', '用于开启和关闭闸门的控制功能，通常通过车辆接口如方向盘面板操作。', 'F001', ARRAY['Convenience','Driving','Physical-button','Body-control','Manual-control','User-triggered']::text[]),
('PF-0131', 'Charging Flap Status Notification', '充电口盖状态通知', 'Provides notifications and warnings related to the charging flap status, such as open/closed state, via mobile app or in-vehicle display to ensure safe and proper charging operations.', '通过手机应用或车载显示屏提供充电口盖状态（如开启/关闭）的通知和警告，以确保安全和正确的充电操作。', 'F005', ARRAY['safety','convenience','charging','mobile-app','IVI','charging','notification','monitoring','event-based','condition-based']::text[]),
('PF-0132', 'Hotspot Configuration Management', '热点配置管理', 'Allows users to modify and manage the vehicle''s hotspot settings, such as name and password, to personalize and secure the Wi-Fi network.', '允许用户修改和管理车辆的热点设置，如名称和密码，以个性化和保护Wi-Fi网络。', 'F005', ARRAY['convenience','security','infotainment','telematics','personalization','manual-control','user-triggered','IVI','mobile-app']::text[]),
('PF-0133', 'USB Device Management', 'USB设备管理', 'Manages the connection, scanning, and data handling of USB devices in the vehicle, including storage access and device recognition.', '管理车辆中USB设备的连接、扫描和数据处理，包括存储访问和设备识别。', 'F023', ARRAY['convenience','entertainment','infotainment','driving','user-triggered','system-triggered']::text[]),
('PF-0134', 'In-Car App Store Management', '车载应用商店管理', 'Management of downloading, installing, and accessing applications from the vehicle''s app store, including app discovery and updates.', '管理从车载应用商店下载、安装和访问应用程序，包括应用发现和更新。', 'F028', ARRAY['Entertainment','Convenience','Infotainment','IVI','User-triggered','Information']::text[]),
('PF-0135', 'Media Play/Pause Control', '媒体播放/暂停控制', 'Controls the basic playback state (play, pause, resume) of various media types including music, video, and radio via multiple interfaces.', '通过多种界面控制音乐、视频和广播等各种媒体类型的基本播放状态（播放、暂停、恢复）。', 'F026', ARRAY['entertainment','convenience','driving','IVI','voice','physical-button','infotainment','audio','manual-control','user-triggered']::text[]),
('PF-0136', 'Voice Media Playback Control', '语音媒体播放控制', 'Control media playback functions such as play, pause, skip, shuffle, and loop modes through voice commands, covering music, video, and radio applications.', '通过语音指令控制媒体播放功能，如播放、暂停、跳过、随机播放和循环模式，涵盖音乐、视频和电台应用。', 'F006', ARRAY['entertainment','convenience','voice','infotainment','audio','user-triggered','manual-control']::text[]),
('PF-0137', 'Playback Mode Switching', '播放模式切换', 'Allows users to switch between different music playback modes such as repeat and shuffle.', '允许用户在音乐应用中切换不同的播放模式，如循环和随机播放。', 'F026', ARRAY['entertainment','audio','infotainment','manual-control','user-triggered']::text[]),
('PF-0138', 'USB Media Directory Management', 'USB媒体目录管理', 'Manages the organization and navigation of audio files from USB devices, including automatic directory creation from multiple folders and manual search capabilities.', '管理来自USB设备的音频文件的组织和导航，包括从多个文件夹自动创建目录和手动搜索功能。', 'F004', ARRAY['entertainment','convenience','driving','IVI','audio','infotainment','efficiency','user-triggered']::text[]),
('PF-0139', 'Voice Media Control', '语音媒体控制', 'Control media playback (e.g., video, music) through voice commands, allowing users to play, pause, or manage media content hands-free.', '通过语音指令控制媒体播放（如视频、音乐），允许用户免提播放、暂停或管理媒体内容。', 'F006', ARRAY['entertainment','convenience','voice','infotainment','manual-control','user-triggered']::text[]),
('PF-0140', 'Basic Video Playback Control', '基础视频播放控制', 'Core video playback operations including play, pause, stop, and progress adjustment, accessible via steering wheel or central display.', '核心视频播放操作，包括播放、暂停、停止和进度调整，可通过方向盘或中央显示屏访问。', 'F027', ARRAY['Entertainment','Driving','IVI','Infotainment','Manual-control','User-triggered']::text[]),
('PF-0141', 'Voice Video Search', '语音视频搜索', 'Allows users to search for video content using voice commands, enabling hands-free access to video media.', '允许用户使用语音命令搜索视频内容，实现免提访问视频媒体。', 'F006', ARRAY['Entertainment','Convenience','Voice','Infotainment','User-triggered']::text[]),
('PF-0142', 'Photo Album Browsing', '相册浏览', 'Allows users to browse and view photos in the in-car photo album application, including thumbnail navigation and image enlargement.', '允许用户在车载相册应用中浏览和查看照片，包括缩略图导航和图片放大功能。', 'F028', ARRAY['Entertainment','Convenience','Driving','IVI','Infotainment','User-triggered','Manual-control']::text[]),
('PF-0143', 'Radio Playback Control', '收音机播放控制', 'Controls for switching radio channels (next/previous) via steering wheel, voice, or app, and displaying current playback status.', '通过方向盘、语音或App切换收音机频道（上一曲/下一曲），并显示当前播放状态。', 'F007', ARRAY['Entertainment','Convenience','Driving','IVI','voice','mobile-app','physical-button','infotainment','Manual-control','User-triggered']::text[]),
('PF-0144', 'Factory Reset Management', '出厂设置管理', 'Allows resetting vehicle systems and ECUs to factory default settings, typically used for troubleshooting, maintenance, or end-of-life processes.', '允许将车辆系统和ECU重置为出厂默认设置，通常用于故障排除、维护或生命周期结束流程。', 'F049', ARRAY['maintenance','security','body-control','powertrain','infotainment','telematics','user-triggered','system-triggered']::text[]),
('PF-0145', 'Charging Station Recommendation', '充电站推荐', 'Intelligent recommendation of charging stations based on battery level and driving range, displayed on the map with route guidance.', '基于电池电量和行驶里程智能推荐充电站，在地图上显示并提供路线引导。', 'F017', ARRAY['Convenience','Navigation','Driving','Charging','IVI','Infotainment','Charging','Efficiency','Automation','Condition-based']::text[]),
('PF-0146', 'Volume Adjustment Control', '音量调节控制', 'Controls for adjusting audio volume, including dynamic volume adjustment and intensity settings, accessible via steering wheel panel or other interfaces.', '调节音频音量的控制功能，包括动态音量调节和强度设置，可通过方向盘面板或其他界面访问。', 'F013', ARRAY['Entertainment','Convenience','Driving','IVI','Physical-button','Audio','Manual-control','User-triggered']::text[]),
('PF-0147', 'Trip Information Display', '行程信息显示', 'Displays travel-related information such as average speed, mileage, driving time, and energy consumption since vehicle startup, providing drivers with insights into their recent journey.', '显示自车辆启动后的行程相关信息，如平均车速、行驶里程、行驶时间、平均电耗等，为驾驶员提供近期行程的洞察。', 'F005', ARRAY['Information','Driving','IVI','Infotainment','Powertrain','Efficiency','Awareness','Monitoring','System-Triggered']::text[]),
('PF-0148', 'Instrument Panel UI Personalization', '仪表板UI个性化', 'Allows users to customize the appearance of the instrument panel interface, including themes, color schemes, and automatic mood-based adjustments.', '允许用户自定义仪表板界面的外观，包括主题、配色方案和基于情绪的自动调整。', 'F008', ARRAY['comfort','convenience','driving','IVI','infotainment','personalization','user-triggered']::text[]),
('PF-0149', 'Incident Media Storage', '事故媒体存储', 'Stores photos and videos related to incidents for user access and review.', '存储与事故相关的照片和视频，供用户访问和查看。', 'F027', ARRAY['security','emergency','infotainment','telematics','monitoring','event-based']::text[]),
('PF-0150', 'Dash Cam Recording Settings', '行车记录仪录制设置', 'Controls and configurations for dash cam recording parameters such as frame rate and resolution.', '控制行车记录仪录制参数，如帧率和分辨率等设置。', 'F027', ARRAY['safety','driving','IVI','ADAS','personalization','user-triggered','monitoring','security']::text[]),
('PF-0151', 'Portable Device Management', '便携设备管理', 'Management and configuration of portable devices connected to the vehicle system, including formatting, data handling, and device settings.', '管理与车辆系统连接的便携设备，包括格式化、数据处理和设备设置。', 'F005', ARRAY['convenience','infotainment','personalization','user-triggered']::text[]),
('PF-0152', 'Emergency System Self-Test Control', '应急系统自检控制', 'Controls the self-test functionality of emergency systems, such as airbags, seatbelt pretensioners, or other safety-critical components, to ensure they are operational and ready for use.', '控制应急系统（如安全气囊、安全带预紧器等安全关键部件）的自检功能，确保其处于可操作和就绪状态。', 'F005', ARRAY['safety','emergency','automation','ADAS','body-control','monitoring','system-triggered','condition-based']::text[]),
('PF-0153', 'Notification Settings & Configuration', '通知设置与配置', 'Manages user preferences, global toggles, and configuration interfaces for system notifications via various input methods (Voice, SWP, App).', '管理用户偏好、全局开关以及通过多种输入方式（语音、方向盘面板、App）进行的系统通知配置界面。', 'F024', ARRAY['Convenience','Communication','Personalization','Notification','Voice','Mobile-App','Infotainment','Telematics','User-triggered','System-triggered']::text[]),
('PF-0154', 'Car Sharing Notification Management', '共享汽车通知管理', 'Controls notification preferences and settings specifically for car sharing vehicles, including sound and alert configurations.', '控制共享汽车的通知偏好和设置，包括声音和警报配置。', 'F004', ARRAY['convenience','car-sharing','mobile-app','telematics','notification','user-triggered','personalization']::text[]),
('PF-0155', 'Traffic Entity Notification via Voice', '语音交通实体通知', 'Provides voice notifications about upcoming traffic entities (e.g., accidents, roadworks, hazards) detected on the route using Road Event Data, delivered via the Voice Assistant.', '通过语音助手提供关于路线上检测到的即将到来的交通实体（如事故、道路施工、危险）的语音通知，使用道路事件数据。', 'F006', ARRAY['safety','information','driving','voice','ADAS','infotainment','telematics','notification','event-based','location-based']::text[]),
('PF-0156', 'Parking Violation Zone Detection', '停车违章区域检测', 'Detects parking violation zones using geodata or ADAS HD to assist drivers in avoiding illegal parking areas.', '使用地理数据或ADAS HD检测停车违章区域，帮助驾驶员避免在非法停车区域停车。', 'F023', ARRAY['safety','convenience','parking','driving','ADAS','infotainment','awareness','notification','location-based','system-triggered']::text[]),
('PF-0157', 'Path Guidance Control', '路径引导控制', 'Controls the guidance process for planned paths, including activation and management of path guidance based on selected planned path data.', '控制规划路径的引导过程，包括基于选定的规划路径数据激活和管理路径引导。', 'F023', ARRAY['navigation','driving','ADAS','automation','user-triggered']::text[]),
('PF-0158', 'Location Bookmark Detection', '位置书签检测', 'Automatically detects and predicts frequently visited locations (e.g., home, work, countryside) based on user patterns to create bookmarks for navigation or personalization.', '基于用户模式自动检测和预测常去地点（如家、工作、乡村），以创建导航或个性化书签。', 'F017', ARRAY['Navigation','Convenience','Efficiency','Driving','Infotainment','Telematics','Personalization','Automation','Location-based']::text[]),
('PF-0159', 'Bookmark Management', '书签管理', 'Manage bookmarks for locations or points of interest, including editing and deleting via steering wheel panel or mobile app', '管理位置或兴趣点的书签，包括通过方向盘面板或移动应用程序进行编辑和删除', 'F028', ARRAY['convenience','navigation','driving','mobile-app','IVI','infotainment','personalization','user-triggered']::text[]),
('PF-0160', 'IVI System Management', '车载信息娱乐系统管理', 'Control and manage the In-Vehicle Infotainment system, including restart, reset, and system-level operations.', '控制和管理车载信息娱乐系统，包括重启、重置和系统级操作。', 'F001', ARRAY['convenience','infotainment','IVI','system-triggered','maintenance']::text[]),
('PF-0161', 'Product Function PF-0161', '', NULL, '', 'F000', ARRAY['Convenience','Body-control','Automation']::text[]),
('PF-0162', 'Cloud Storage Management', '云端存储管理', 'Manages cloud-based storage for vehicle data, media, and other content, enabling remote access and backup.', '管理车辆数据、媒体和其他内容的云端存储，支持远程访问和备份。', 'F022', ARRAY['Convenience','Remote','Telematics','Infotainment','Monitoring','User-triggered','Information','Security']::text[]),
('PF-0163', 'Photo Capture Control', '拍照控制', 'Controls for taking photos from the vehicle, including via steering wheel panel or voice assistant.', '控制从车辆拍照的功能，包括通过方向盘面板或语音助手拍照。', 'F006', ARRAY['convenience','driving','voice','physical-button','infotainment','manual-control','user-triggered']::text[]),
('PF-0164', 'Welcome Greetings Control', '欢迎问候语控制', 'Controls welcome greetings and related display or audio messages when approaching or entering the vehicle.', '控制接近或进入车辆时的欢迎问候语及相关显示或音频消息。', 'F031', ARRAY['Convenience','Comfort','Pre-trip','Post-trip','Infotainment','Audio','Personalization','System-triggered','Location-based','Event-based']::text[]),
('PF-0165', 'DVR Camera Selection Control', '行车记录仪摄像头选择控制', 'Allows users to select which DVR cameras (front, back, cabin) are active for recording in general recording mode, with options to choose one, several, or all cameras via steering wheel panel or mobile app.', '允许用户在一般录像模式下选择行车记录仪的摄像头（前、后、舱内）进行录制，可通过方向盘面板或手机应用选择一个、多个或全部摄像头。', 'F008', ARRAY['safety','security','driving','mobile-app','physical-button','infotainment','ADAS','manual-control','user-triggered']::text[]),
('PF-0166', 'DVR Recording Configuration', '行车记录仪录制配置', 'User configuration of DVR recording settings, such as segment length, to customize how driving events are recorded.', '用户配置行车记录仪录制设置，如片段长度，以自定义驾驶事件的录制方式。', 'F012', ARRAY['safety','driving','IVI','ADAS','infotainment','personalization','manual-control','user-triggered']::text[]),
('PF-0167', 'Storage Management', '存储管理', 'Manages storage capacity, folders, and configuration for vehicle systems, such as determining storage size states and organizing stored information.', '管理车辆系统的存储容量、文件夹和配置，例如确定存储大小状态和组织存储信息。', 'F035', ARRAY['Information','Infotainment','Telematics','System-Triggered','Monitoring','Efficiency']::text[]),
('PF-0168', 'Digital Interior Mirror Configuration', '数字车内后视镜配置', 'Allows the driver to configure settings for the digital interior mirror, such as dynamic lines and brightness, to personalize the display and enhance visibility.', '允许驾驶员配置数字车内后视镜的设置，如动态线条和亮度，以个性化显示并增强可见性。', 'F047', ARRAY['comfort','safety','driving','IVI','ADAS','body-control','personalization','user-triggered']::text[]),
('PF-0169', 'AI Conversation Management', 'AI对话管理', 'Provides AI-powered conversational responses and small talk interactions to users via the vehicle system.', '通过车辆系统向用户提供AI驱动的对话响应和小型聊天互动。', 'F022', ARRAY['communication','entertainment','infotainment','voice','user-triggered']::text[]),
('PF-0170', 'HUD Brightness Control', 'HUD亮度控制', 'Allows the driver to adjust the brightness of the Head-Up Display (HUD) through various control channels such as voice assistant, IVI, or mobile app.', '允许驾驶员通过语音助手、IVI或手机应用等多种控制渠道调节抬头显示（HUD）的亮度。', 'F006', ARRAY['comfort','safety','driving','IVI','voice','mobile-app','lighting','infotainment','personalization','manual-control','user-triggered']::text[]),
('PF-0171', 'IVI Application Network Access Control', 'IVI应用网络访问控制', 'Allows users to configure internet access methods for IVI applications, such as routing via e-sim, Wi-Fi, or blocking access, based on application settings.', '允许用户根据应用设置配置IVI应用的互联网访问方式，例如通过e-sim、Wi-Fi路由或阻止访问。', 'F028', ARRAY['convenience','security','IVI','infotainment','telematics','personalization','user-triggered','monitoring']::text[]),
('PF-0172', 'Digital User Manual Access', '数字用户手册访问', 'Allows users to access and view the electronic user manual of the vehicle through various interfaces such as the steering wheel panel.', '允许用户通过方向盘面板等多种界面访问和查看车辆的电子用户手册。', 'F001', ARRAY['Information','Convenience','Driving','Parking','IVI','Infotainment','Body-control','Experience_type: awareness','User-triggered']::text[]),
('PF-0173', 'Travel History Storage', '旅行历史存储', 'Stores and manages travel history data for user reference and analysis.', '存储和管理旅行历史数据，供用户参考和分析。', 'F028', ARRAY['convenience','information','post-trip','infotainment','telematics','monitoring','user-triggered','location-based']::text[]),
('PF-0174', 'Map Orientation Control', '地图方向控制', 'Allows users to adjust the orientation of the map view (e.g., north-up, heading-up) for better navigation and situational awareness.', '允许用户调整地图视图的方向（例如，北向上、车头向上），以提供更好的导航和态势感知。', 'F011', ARRAY['navigation','driving','IVI','infotainment','awareness','manual-control','user-triggered']::text[]),
('PF-0175', 'Map Settings Control', '地图设置控制', 'Controls map display settings such as rendering, orientation, and customization for navigation and driving assistance.', '控制地图显示设置，如渲染、方向和导航及驾驶辅助的自定义。', 'F011', ARRAY['navigation','driving','IVI','ADAS','infotainment','personalization','manual-control','user-triggered']::text[]),
('PF-0176', 'Offline Map Storage Management', '离线地图存储管理', 'Manages the storage and handling of downloaded offline map data for navigation purposes.', '管理下载的离线地图数据的存储和处理，用于导航目的。', 'F028', ARRAY['navigation','driving','infotainment','convenience','location-based','efficiency']::text[]),
('PF-0177', 'Remote High-Voltage System Control', '远程高压系统控制', 'Allows users to remotely power on or off the high-voltage system, typically for pre-conditioning or energy management purposes.', '允许用户远程开启或关闭高压系统，通常用于预调节或能源管理目的。', 'F002', ARRAY['convenience','efficiency','remote','mobile-app','powertrain','charging','manual-control','user-triggered']::text[]),
('PF-0178', 'Network Wakeup Management', '网络唤醒管理', 'Manages the wakeup mechanisms for network communication in different vehicle power states, ensuring ECUs can function based on PNM or NM protocols during high-voltage OFF and low-voltage OFF scenarios.', '管理不同车辆电源状态下的网络通信唤醒机制，确保在高压关闭和低压关闭功能场景下，ECU能够基于PNM或NM协议工作。', 'F012', ARRAY['communication','convenience','telematics','body-control','efficiency','automation','system-triggered','condition-based']::text[]),
('PF-0179', 'Battery Health Monitoring', '电池健康监控', 'Monitors battery state of charge (SOC) and detects abnormal declines, logging potential causes such as DCDC output limitations, failures, communication issues, or power load exceedances to ensure battery performance and safety.', '监控电池荷电状态（SOC），检测异常下降，并记录潜在原因（如DCDC输出限制、故障、通信中断或功率负载超限），以确保电池性能和安全。', 'F017', ARRAY['safety','powertrain','monitoring','system-triggered','condition-based','awareness','maintenance']::text[]),
('PF-0180', 'Low-voltage Energy Management', '低压能量管理', 'Controls and monitors the low-voltage battery system to optimize energy usage and prevent battery drain, including functions like closing energy management limits based on SOC trends.', '控制和监控低压电池系统，以优化能量使用并防止电池耗尽，包括根据SOC趋势关闭能量管理限制等功能。', 'F017', ARRAY['efficiency','powertrain','body-control','monitoring','automation','system-triggered','condition-based']::text[]),
('PF-0181', 'Vehicle Power Mode Management', '车辆电源模式管理', 'Controls and monitors the vehicle''s power states (e.g., standby, normal, ready) and related system checks during transitions, ensuring all systems are properly energized and faults are reported to the user via the IVI.', '控制和监控车辆的电源状态（例如待机、正常、就绪）以及转换期间的相关系统检查，确保所有系统正常通电并通过IVI向用户报告故障。', 'F012', ARRAY['Convenience','Safety','Powertrain','Body-control','Monitoring','System-triggered','Condition-based','Notification','IVI']::text[]),
('PF-0182', 'Power Mode and Fault Management', '电源模式与故障管理', 'Manages vehicle power states (e.g., low voltage, high voltage) and handles fault conditions related to power systems, ensuring proper power transitions and fault notifications.', '管理车辆电源状态（如低电压、高电压）并处理与电源系统相关的故障条件，确保正确的电源转换和故障通知。', 'F017', ARRAY['safety','efficiency','powertrain','body-control','charging','monitoring','system-triggered','condition-based','notification','emergency']::text[]),
('PF-0183', 'High Voltage System Management', '高压系统管理', 'Manages the activation, control, and monitoring of the vehicle''s high voltage system for scenarios requiring high voltage operation.', '管理车辆高压系统的激活、控制和监控，适用于需要高压操作的功能场景。', 'F038', ARRAY['safety','efficiency','driving','powertrain','monitoring','system-triggered']::text[]),
('PF-0184', 'ECU Replacement and Commissioning Management', 'ECU更换与调试管理', 'Manages the storage and execution of procedures for ECU replacement, including commissioning, VIN storage, calibration, and coding during EOL plant processes or vehicle repair.', '管理ECU更换过程中的程序存储与执行，包括整车下线流程或车辆维修期间的ECU调试、VIN存储、校准和编码。', 'F035', ARRAY['convenience','efficiency','maintenance','automation','powertrain','body-control','system-triggered']::text[]),
('PF-0185', 'Vehicle State Determination', '车辆状态确定', 'Determines and synchronizes the current state of the vehicle, such as power mode, operational status, or system readiness, to support user awareness and system coordination.', '确定并同步车辆的当前状态，如电源模式、运行状态或系统准备情况，以支持用户感知和系统协调。', 'F001', ARRAY['Information','Awareness','System-Triggered','Powertrain','Body-Control','Security','Telematics','Monitoring']::text[]),
('PF-0186', 'Service Mode Management', '服务模式管理', 'Allows the driver to activate and configure service mode settings for vehicle sharing or maintenance, including time, mileage, geolocation restrictions, and notification preferences.', '允许驾驶员激活和配置车辆共享或维护的服务模式设置，包括时间、里程、地理位置限制和通知偏好。', 'F004', ARRAY['convenience','security','car-sharing','maintenance','IVI','mobile-app','telematics','body-control','personalization','monitoring','user-triggered','time-based','location-based']::text[]),
('PF-0187', 'Vital Signs Monitoring and Diagnostics', '生命体征监测与诊断', 'Collects and analyzes vehicle vitals (e.g., ECU/HPC data) through scheduled or event-based triggers, generates diagnostic data, and stores it locally and in the cloud for health monitoring and maintenance purposes.', '通过计划或事件触发收集和分析车辆生命体征（如ECU/HPC数据），生成诊断数据，并存储在车辆和云端，用于健康监测和维护。', 'F040', ARRAY['safety','efficiency','maintenance','driving','telematics','powertrain','body-control','monitoring','system-triggered','event-based','time-based']::text[]),
('PF-0188', 'Remote Diagnostic Rule Execution', '远程诊断规则执行', 'Executes diagnostic rules remotely triggered by cloud requests, analyzes ECU/HPC vitals, generates diagnostic data, and stores it in the vehicle and cloud for monitoring and troubleshooting.', '通过云端请求远程触发执行诊断规则，分析ECU/HPC关键指标，生成诊断数据，并存储在车辆和云端，用于监控和故障排除。', 'F022', ARRAY['convenience','efficiency','maintenance','remote','telematics','powertrain','body-control','monitoring','system-triggered','condition-based']::text[]),
('PF-0189', 'Scheduled Maintenance Management', '定期维护管理', 'Manages vehicle service intervals based on time or mileage, including reminders and scheduling for maintenance tasks.', '基于时间或里程管理车辆服务间隔，包括维护任务的提醒和调度。', 'F039', ARRAY['convenience','efficiency','maintenance','schedule','telematics','notification','automation','time-based','condition-based']::text[]),
('PF-0190', 'Vehicle Readiness Indicator Control', '车辆就绪指示灯控制', 'Controls the indicator lamp that signals when the vehicle is ready for driving, providing visual feedback to the driver about the vehicle''s operational status.', '控制车辆就绪指示灯，当车辆准备就绪可行驶时提供视觉反馈，告知驾驶员车辆运行状态。', 'F001', ARRAY['safety','driving','body-control','feedback','system-triggered','awareness','information']::text[]),
('PF-0191', 'Vehicle Diagnostic Topology Exploration', '车辆诊断拓扑探索', 'Allows users to explore and view the vehicle''s diagnostic system topology, including connected components and their status, typically accessed through diagnostic tools or interfaces.', '允许用户探索和查看车辆诊断系统的拓扑结构，包括连接的组件及其状态，通常通过诊断工具或界面访问。', 'F005', ARRAY['information','maintenance','IVI','telematics','body-control','monitoring','user-triggered']::text[]),
('PF-0192', 'Diagnostic Data Reading', '诊断数据读取', 'Reading diagnostic data from vehicle ECUs/HPCs using UDS commands, typically for maintenance or troubleshooting purposes.', '使用UDS命令从车辆ECU/HPC读取诊断数据，通常用于维护或故障排除目的。', 'F005', ARRAY['information','maintenance','telematics','monitoring','user-triggered','system-triggered','condition-based']::text[]),
('PF-0193', 'Diagnostic Data Writing', '诊断数据写入', 'Allows writing diagnostic data (e.g., DIDs) to vehicle ECUs/HPCs using diagnostic tools like DAST in proximity, typically for configuration, calibration, or troubleshooting purposes.', '允许使用诊断工具（如近距离DAST）向车辆ECU/HPC写入诊断数据（例如DID），通常用于配置、校准或故障排除。', 'F005', ARRAY['maintenance','efficiency','powertrain','body-control','telematics','user-triggered','monitoring']::text[]),
('PF-0194', 'Remote Diagnostic Routine Execution', '远程诊断程序执行', 'Allows remote execution of diagnostic routines on vehicle ECUs/HPCs via unified diagnostic services for troubleshooting and maintenance.', '允许通过统一诊断服务远程执行车辆ECU/HPC的诊断程序，用于故障排除和维护。', 'F002', ARRAY['convenience','maintenance','remote','telematics','body-control','powertrain','monitoring','user-triggered']::text[]),
('PF-0195', 'Remote Diagnostic Data Writing', '远程诊断数据写入', 'Allows writing diagnostic data (e.g., DIDs) to vehicle ECUs/HPCs remotely via diagnostic tools or portals, typically for configuration, calibration, or troubleshooting purposes.', '允许通过诊断工具或门户远程向车辆ECU/HPC写入诊断数据（如DID），通常用于配置、校准或故障排除。', 'F002', ARRAY['convenience','efficiency','remote','telematics','body-control','powertrain','security','monitoring','system-triggered','user-triggered','maintenance']::text[]),
('PF-0196', 'System Update Management', '系统更新管理', 'Manages the process of checking and executing system updates for the vehicle, including verifying preconditions, downloading updates, and applying them.', '管理车辆系统更新的检查和执行过程，包括验证前提条件、下载更新和应用更新。', 'F012', ARRAY['Convenience','Security','Maintenance','Telematics','System-Triggered','Automation','Notification']::text[]),
('PF-0197', 'National Standard Data Reporting', '国家标准数据上报', 'Provides real-time data reporting according to national standards, typically for regulatory compliance or telematics purposes.', '根据国家标准提供实时数据上报，通常用于法规遵从或远程信息处理目的。', 'F023', ARRAY['information','telematics','driving','monitoring','system-triggered','regulation','compliance']::text[]),
('PF-0198', 'Remote Vehicle Immobilization', '远程车辆禁止启动', 'Allows users to remotely prevent the vehicle from starting via mobile app or other remote methods, typically for security, anti-theft, or fleet management purposes.', '允许用户通过手机应用或其他远程方式远程禁止车辆启动，通常用于安全、防盗或车队管理目的。', 'F022', ARRAY['security','remote','mobile-app','telematics','monitoring','user-triggered']::text[]),
('PF-0199', 'User Authentication Management', '用户身份验证管理', 'Manages user verification and authentication processes for accessing vehicle features or personalization settings.', '管理用户验证和身份验证过程，用于访问车辆功能或个性化设置。', 'F001', ARRAY['security','convenience','security','infotainment','telematics','user-triggered','personalization']::text[]),
('PF-0200', 'Call Initiation and Dialing', '拨打电话与拨号', 'Manages outgoing calls, including dialing, contact selection, and call setup.', '管理去电，包括拨号、联系人选择和通话设置。', 'F012', ARRAY['communication','driving','IVI','telematics','manual-control','user-triggered']::text[])
